import React, { useState, useEffect } from 'react';
import { receptionistAPI } from '../../api/endpoints';
import { showToast } from '../../components/Toast/Toast';

const Billing = () => {
  const [billings, setBillings] = useState([]);

  useEffect(() => {
    fetchBillings();
  }, []);

  const fetchBillings = async () => {
    try {
      const response = await receptionistAPI.getBilling();
      setBillings(response.data);
    } catch (error) {
      console.error('Error fetching billings:', error);
    }
  };

  const handlePayment = async (billingId, paymentMethod = 'cash') => {
    try {
      const response = await receptionistAPI.markPaid(billingId, { payment_method: paymentMethod });
      if (response.data.success) {
        showToast('Payment processed successfully!', 'success');
        fetchBillings();
      }
    } catch (error) {
      showToast('Payment processing failed. Please try again.', 'error');
      console.error('Payment processing failed:', error);
    }
  };

  return (
    <div className="billing">
      <h2>Billing Management</h2>
      <div className="billing-list">
        {billings.map(billing => (
          <div key={billing.id} className="billing-card">
            <p>Patient: {billing.patient_name}</p>
            <p>Amount: ${billing.amount}</p>
            <p>Status: {billing.status}</p>
            <p>Date: {billing.created_at}</p>
            {billing.status === 'pending' && (
              <div className="payment-actions">
                <button onClick={() => handlePayment(billing.id, 'cash')}>Cash Payment</button>
                <button onClick={() => handlePayment(billing.id, 'mpesa')}>M-Pesa Payment</button>
                <button onClick={() => handlePayment(billing.id, 'card')}>Card Payment</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Billing;
