import React, { useState, useEffect } from 'react';
import './SystemSettings.css';

const SystemSettings = () => {
  const [activeTab, setActiveTab] = useState('users');
  const [users, setUsers] = useState([]);
  const [patients, setPatients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteTarget, setDeleteTarget] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  useEffect(() => {
    fetchUsers();
    fetchPatients();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/admin/all-staff', {
        credentials: 'include'
      });
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/admin/all-patients', {
        credentials: 'include'
      });
      const data = await response.json();
      setPatients(data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleDelete = async () => {
    try {
      const endpoint = deleteTarget.type === 'user' ? 'delete-user' : 'delete-patient';
      await fetch(`http://localhost:5000/api/admin/${endpoint}/${deleteTarget.id}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      if (deleteTarget.type === 'user') {
        fetchUsers();
      } else {
        fetchPatients();
      }

      setShowDeleteModal(false);
      setDeleteTarget(null);
      alert(`${deleteTarget.type === 'user' ? 'User' : 'Patient'} deleted successfully`);
    } catch (error) {
      console.error('Error deleting:', error);
      alert('Error deleting record');
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/change-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          current_password: passwordData.currentPassword,
          new_password: passwordData.newPassword
        })
      });

      const result = await response.json();

      if (result.success) {
        alert('Password changed successfully');
        setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      } else {
        alert(result.message || 'Failed to change password');
      }
    } catch (error) {
      console.error('Error changing password:', error);
      alert('Error changing password');
    }
  };

  const openEditModal = (user) => {
    setEditUser({...user, newPassword: '', confirmPassword: ''});
    setShowEditModal(true);
  };

  const handleEditUser = async (e) => {
    e.preventDefault();

    if (editUser.newPassword && editUser.newPassword !== editUser.confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    try {
      const updateData = {
        name: editUser.name,
        email: editUser.email,
        department: editUser.department
      };

      if (editUser.newPassword) {
        updateData.password = editUser.newPassword;
      }

      await fetch(`http://localhost:5000/api/admin/update-user/${editUser.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(updateData)
      });

      fetchUsers();
      setShowEditModal(false);
      setEditUser(null);
      alert('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
      alert('Error updating user');
    }
  };

  const confirmDelete = (item, type) => {
    setDeleteTarget({ ...item, type });
    setShowDeleteModal(true);
  };

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.phone.includes(searchTerm)
  );

  return (
    <div className="system-settings">
      <div className="settings-header">
        <h1>‚öôÔ∏è System Settings</h1>
        <p>Manage users, patients, and system configurations</p>
      </div>

      <div className="settings-tabs">
        <button 
          className={activeTab === 'users' ? 'active' : ''} 
          onClick={() => setActiveTab('users')}
        >
          üë• User Management
        </button>
        <button 
          className={activeTab === 'patients' ? 'active' : ''} 
          onClick={() => setActiveTab('patients')}
        >
          üè• Patient Management
        </button>
        <button 
          className={activeTab === 'system' ? 'active' : ''} 
          onClick={() => setActiveTab('system')}
        >
          üîß System Config
        </button>
        <button 
          className={activeTab === 'password' ? 'active' : ''} 
          onClick={() => setActiveTab('password')}
        >
          üîê Change Password
        </button>
      </div>

      <div className="search-section">
        <input
          type="text"
          placeholder={`Search ${activeTab}...`}
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      {activeTab === 'users' && (
        <div className="management-section">
          <h3>User Management</h3>
          <div className="items-grid">
            {filteredUsers.map(user => (
              <div key={user.id} className="item-card user-card">
                <div className="item-header">
                  <h4>{user.name}</h4>
                  <span className={`role-badge ${user.role}`}>{user.role}</span>
                </div>
                <div className="item-details">
                  <p>üìß {user.email}</p>
                  <p>üè¢ {user.department}</p>
                  <p>üìä {user.status}</p>
                </div>
                <div className="item-actions">
                  <button className="edit-btn" onClick={() => openEditModal(user)}>‚úèÔ∏è Edit</button>
                  <button 
                    className="delete-btn"
                    onClick={() => confirmDelete(user, 'user')}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'patients' && (
        <div className="management-section">
          <h3>Patient Management</h3>
          <div className="items-grid">
            {filteredPatients.map(patient => (
              <div key={patient.id} className="item-card patient-card">
                <div className="item-header">
                  <h4>{patient.name}</h4>
                  <span className="age-badge">{patient.age}y</span>
                </div>
                <div className="item-details">
                  <p>üìû {patient.phone}</p>
                  <p>üë§ {patient.gender}</p>
                  <p>üè• {patient.department}</p>
                </div>
                <div className="item-actions">
                  <button className="edit-btn">‚úèÔ∏è Edit</button>
                  <button 
                    className="delete-btn"
                    onClick={() => confirmDelete(patient, 'patient')}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'system' && (
        <div className="management-section">
          <h3>System Configuration</h3>
          <div className="config-grid">
            <div className="config-card">
              <h4>üè• Hospital Information</h4>
              <div className="config-item">
                <label>Hospital Name</label>
                <input type="text" defaultValue="General Hospital" />
              </div>
              <div className="config-item">
                <label>Address</label>
                <input type="text" defaultValue="123 Medical Street" />
              </div>
              <button className="save-btn">üíæ Save Changes</button>
            </div>

            <div className="config-card">
              <h4>üí∞ Billing Settings</h4>
              <div className="config-item">
                <label>Default Consultation Fee</label>
                <input type="number" defaultValue="50" />
              </div>
              <div className="config-item">
                <label>Currency</label>
                <select defaultValue="USD">
                  <option value="USD">USD ($)</option>
                  <option value="KES">KES (KSh)</option>
                </select>
              </div>
              <button className="save-btn">üíæ Save Changes</button>
            </div>

            <div className="config-card">
              <h4>üîê Security Settings</h4>
              <div className="config-item">
                <label>Session Timeout (minutes)</label>
                <input type="number" defaultValue="30" />
              </div>
              <div className="config-item">
                <label>Password Policy</label>
                <select defaultValue="medium">
                  <option value="low">Low Security</option>
                  <option value="medium">Medium Security</option>
                  <option value="high">High Security</option>
                </select>
              </div>
              <button className="save-btn">üíæ Save Changes</button>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'password' && (
        <div className="management-section">
          <h3>Change Password</h3>
          <div className="password-section">
            <form onSubmit={handlePasswordChange} className="password-form">
              <div className="config-item">
                <label>üîí Current Password</label>
                <input
                  type="password"
                  value={passwordData.currentPassword}
                  onChange={(e) => setPasswordData({...passwordData, currentPassword: e.target.value})}
                  required
                />
              </div>
              <div className="config-item">
                <label>üÜï New Password</label>
                <input
                  type="password"
                  value={passwordData.newPassword}
                  onChange={(e) => setPasswordData({...passwordData, newPassword: e.target.value})}
                  required
                />
              </div>
              <div className="config-item">
                <label>‚úÖ Confirm New Password</label>
                <input
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={(e) => setPasswordData({...passwordData, confirmPassword: e.target.value})}
                  required
                />
              </div>
              <button type="submit" className="save-btn">
                üîê Change Password
              </button>
            </form>
          </div>
        </div>
      )}

      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="delete-modal">
            <div className="modal-header">
              <h3>‚ö†Ô∏è Confirm Deletion</h3>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to delete <strong>{deleteTarget?.name}</strong>?</p>
              <p className="warning-text">This action cannot be undone.</p>
            </div>
            <div className="modal-actions">
              <button 
                className="cancel-btn"
                onClick={() => setShowDeleteModal(false)}
              >
                Cancel
              </button>
              <button 
                className="confirm-delete-btn"
                onClick={handleDelete}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SystemSettings;
