import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { doctorAPI } from '../../api/endpoints';
import './MyPatients.css';

const MyPatients = () => {
  const [patients, setPatients] = useState([]);
  const [filteredPatients, setFilteredPatients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchPatients();
  }, []);

  useEffect(() => {
    const filtered = patients.filter(patient =>
      patient.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      patient.phone.includes(searchTerm)
    );
    setFilteredPatients(filtered);
  }, [patients, searchTerm]);

  const fetchPatients = async () => {
    try {
      const response = await doctorAPI.getMyPatients();
      setPatients(response.data);
      setFilteredPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handlePatientClick = (patientId) => {
    navigate(`/patient/${patientId}`);
  };

  const startExam = (patientId) => {
    navigate(`/doctor/exam/${patientId}`);
  };

  return (
    <div className="my-patients">
      <h2>My Patients</h2>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search patients by name or phone..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="patients-grid">
        {filteredPatients.map(patient => (
          <div key={patient.id} className="patient-card" onClick={() => handlePatientClick(patient.id)} style={{cursor: 'pointer'}}>
            <h3>{patient.name}</h3>
            <p>Age: {patient.age}</p>
            <p>Gender: {patient.gender}</p>
            <p>Phone: {patient.phone}</p>
            <p>Last Visit: {patient.last_visit ? new Date(patient.last_visit).toLocaleDateString() : 'No visits'}</p>
            <div className="patient-actions">
              <button onClick={(e) => { e.stopPropagation(); handlePatientClick(patient.id); }}>View Details</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPatients;
