import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Toast from '../../components/Toast/Toast';
import { useToast } from '../../hooks/useToast';
import './ExamRoom.css';

const ExamRoom = () => {
  const { toasts, success, error } = useToast();
  const { patientId } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);
  const [vitals, setVitals] = useState({
    blood_pressure: '',
    heart_rate: '',
    temperature: '',
    weight: '',
    height: '',
    oxygen_saturation: ''
  });
  const [consultation, setConsultation] = useState({
    symptoms: '',
    diagnosis: '',
    prescription: '',
    notes: '',
    amount: 50,
    payment_method: 'cash'
  });
  const [examData, setExamData] = useState({});

  useEffect(() => {
    fetchPatientDetails();
  }, [patientId]);

  const fetchPatientDetails = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/doctor/patient-details/${patientId}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setPatient(data);
    } catch (error) {
      console.error('Error fetching patient details:', error);
    }
  };

  const recordVitals = async () => {
    try {
      await fetch(`http://localhost:5000/api/vitals/record/${patientId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(vitals)
      });
      success('Vitals recorded successfully');
      // Clear vitals form
      setVitals({
        blood_pressure: '',
        heart_rate: '',
        temperature: '',
        weight: '',
        height: '',
        oxygen_saturation: ''
      });
    } catch (error) {
      console.error('Error recording vitals:', error);
    }
  };

  const saveConsultation = async () => {
    if (!consultation.diagnosis.trim()) {
      error('Please enter a diagnosis');
      return;
    }

    console.log('Saving consultation with exam data:', examData);

    try {
      const response = await fetch('http://localhost:5000/api/doctor/save-consultation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ 
          ...consultation, 
          patient_id: parseInt(patientId),
          exam_data: JSON.stringify(examData),
          amount: parseFloat(consultation.amount) || 50,
          symptoms: consultation.symptoms || 'Not specified'
        })
      });

      const result = await response.json();
      if (result.success) {
        success('Consultation saved and sent to billing successfully');
        setTimeout(() => navigate('/doctor/dashboard'), 1500);
      } else {
        error(result.message || 'Error saving consultation');
      }
    } catch (error) {
      console.error('Error saving consultation:', error);
      error('Error saving consultation');
    }
  };

  const renderDepartmentSpecificForm = () => {
    if (!patient) return null;

    switch(patient.department) {
      case 'eye':
        return renderEyeForm();
      case 'ent':
        return renderENTForm();
      case 'skin':
        return renderSkinForm();
      default:
        return null;
    }
  };

  const renderEyeForm = () => (
    <div className="eye-exam-form">
      <div className="eye-section">
        <h4>üëÅ Right Eye (OD)</h4>
        <input placeholder="Visual Acuity" value={examData.od_visual_acuity || ''} onChange={(e) => setExamData({...examData, od_visual_acuity: e.target.value})} />
        <input placeholder="Cornea" value={examData.od_cornea || ''} onChange={(e) => setExamData({...examData, od_cornea: e.target.value})} />
        <input placeholder="Lens" value={examData.od_lens || ''} onChange={(e) => setExamData({...examData, od_lens: e.target.value})} />
        <input placeholder="Retina" value={examData.od_retina || ''} onChange={(e) => setExamData({...examData, od_retina: e.target.value})} />
        <input placeholder="Intraocular Pressure" value={examData.od_pressure || ''} onChange={(e) => setExamData({...examData, od_pressure: e.target.value})} />
        <input placeholder="Refraction" value={examData.od_refraction || ''} onChange={(e) => setExamData({...examData, od_refraction: e.target.value})} />
      </div>
      <div className="eye-section">
        <h4>üëÅ Left Eye (OS)</h4>
        <input placeholder="Visual Acuity" value={examData.os_visual_acuity || ''} onChange={(e) => setExamData({...examData, os_visual_acuity: e.target.value})} />
        <input placeholder="Cornea" value={examData.os_cornea || ''} onChange={(e) => setExamData({...examData, os_cornea: e.target.value})} />
        <input placeholder="Lens" value={examData.os_lens || ''} onChange={(e) => setExamData({...examData, os_lens: e.target.value})} />
        <input placeholder="Retina" value={examData.os_retina || ''} onChange={(e) => setExamData({...examData, os_retina: e.target.value})} />
        <input placeholder="Intraocular Pressure" value={examData.os_pressure || ''} onChange={(e) => setExamData({...examData, os_pressure: e.target.value})} />
        <input placeholder="Refraction" value={examData.os_refraction || ''} onChange={(e) => setExamData({...examData, os_refraction: e.target.value})} />
      </div>
    </div>
  );

  const renderENTForm = () => (
    <div className="ent-exam-form">
      <h4>üëÇ ENT Examination</h4>
      <input placeholder="Right Ear Exam" value={examData.right_ear || ''} onChange={(e) => setExamData({...examData, right_ear: e.target.value})} />
      <input placeholder="Left Ear Exam" value={examData.left_ear || ''} onChange={(e) => setExamData({...examData, left_ear: e.target.value})} />
      <input placeholder="Nose Exam" value={examData.nose_exam || ''} onChange={(e) => setExamData({...examData, nose_exam: e.target.value})} />
      <input placeholder="Throat Exam" value={examData.throat_exam || ''} onChange={(e) => setExamData({...examData, throat_exam: e.target.value})} />
      <input placeholder="Hearing Test" value={examData.hearing_test || ''} onChange={(e) => setExamData({...examData, hearing_test: e.target.value})} />
    </div>
  );

  const renderSkinForm = () => (
    <div className="skin-exam-form">
      <h4>ü©∫ Skin Examination</h4>
      <input placeholder="Affected Area" value={examData.affected_area || ''} onChange={(e) => setExamData({...examData, affected_area: e.target.value})} />
      <input placeholder="Lesion Type" value={examData.lesion_type || ''} onChange={(e) => setExamData({...examData, lesion_type: e.target.value})} />
      <input placeholder="Color/Texture" value={examData.color_texture || ''} onChange={(e) => setExamData({...examData, color_texture: e.target.value})} />
      <textarea placeholder="Symptoms" value={examData.symptoms || ''} onChange={(e) => setExamData({...examData, symptoms: e.target.value})} />
    </div>
  );

  if (!patient) return <div>Loading...</div>;

  return (
    <div className="exam-room">
      <div className="exam-header">
        <button onClick={() => navigate(-1)}>‚Üê Back</button>
        <h2>Examination - {patient.name}</h2>
      </div>

      <div className="exam-sections">
        <div className="vitals-section">
          <h3>Record Vitals</h3>
          <div className="vitals-form">
            <input
              type="text"
              placeholder="Blood Pressure (e.g. 120/80)"
              value={vitals.blood_pressure}
              onChange={(e) => setVitals({...vitals, blood_pressure: e.target.value})}
            />
            <input
              type="number"
              placeholder="Heart Rate"
              value={vitals.heart_rate}
              onChange={(e) => setVitals({...vitals, heart_rate: e.target.value})}
            />
            <input
              type="number"
              placeholder="Temperature (¬∞C)"
              value={vitals.temperature}
              onChange={(e) => setVitals({...vitals, temperature: e.target.value})}
            />
            <input
              type="number"
              placeholder="Weight (kg)"
              value={vitals.weight}
              onChange={(e) => setVitals({...vitals, weight: e.target.value})}
            />
            <input
              type="number"
              placeholder="Height (cm)"
              value={vitals.height}
              onChange={(e) => setVitals({...vitals, height: e.target.value})}
            />
            <input
              type="number"
              placeholder="Oxygen Saturation (%)"
              value={vitals.oxygen_saturation}
              onChange={(e) => setVitals({...vitals, oxygen_saturation: e.target.value})}
            />
            <button onClick={recordVitals}>Record Vitals</button>
          </div>
        </div>

        <div className="consultation-section">
          <h3>Consultation</h3>
          {renderDepartmentSpecificForm()}
          <div className="general-section">
            <h4>General</h4>
            <textarea
              placeholder="Symptoms (Chief Complaint)"
              value={consultation.symptoms}
              onChange={(e) => setConsultation({...consultation, symptoms: e.target.value})}
            />
            <textarea
              placeholder="Diagnosis"
              value={consultation.diagnosis}
              onChange={(e) => setConsultation({...consultation, diagnosis: e.target.value})}
            />
            <textarea
              placeholder="Prescription (medicines + dosage)"
              value={consultation.prescription}
              onChange={(e) => setConsultation({...consultation, prescription: e.target.value})}
            />
            <textarea
              placeholder="Notes / Recommendations"
              value={consultation.notes}
              onChange={(e) => setConsultation({...consultation, notes: e.target.value})}
            />
            <div className="billing-section">
              <h4>üí∞ Billing</h4>
              <input
                type="number"
                placeholder="Consultation Fee"
                value={consultation.amount || ''}
                onChange={(e) => setConsultation({...consultation, amount: e.target.value})}
              />
              <select
                value={consultation.payment_method || 'cash'}
                onChange={(e) => setConsultation({...consultation, payment_method: e.target.value})}
              >
                <option value="cash">Cash</option>
                <option value="mpesa">M-Pesa</option>
                <option value="card">Card</option>
              </select>
            </div>
            <button onClick={saveConsultation}>Save Consultation & Send to Billing</button>
          </div>
        </div>
      </div>

      {toasts.map(toast => (
        <Toast
          key={toast.id}
          message={toast.message}
          type={toast.type}
          duration={toast.duration}
          onClose={() => {}}
        />
      ))}
    </div>
  );
};

export default ExamRoom;
