import React, { useState, useEffect } from 'react';
import { receptionistAPI } from '../../api/endpoints';

const Queue = () => {
  const [queue, setQueue] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    patientId: '',
    department: '',
    priority: 'normal'
  });

  useEffect(() => {
    fetchQueue();
  }, []);

  const fetchQueue = async () => {
    try {
      const response = await receptionistAPI.getQueue();
      setQueue(response.data);
    } catch (error) {
      console.error('Error fetching queue:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await receptionistAPI.addToQueue(formData);
      alert('Patient added to queue');
      setShowForm(false);
      fetchQueue();
    } catch (error) {
      alert('Failed to add to queue');
    }
  };

  return (
    <div className="queue">
      <div className="queue-header">
        <h2>Patient Queue</h2>
        <button onClick={() => setShowForm(true)}>Add to Queue</button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="queue-form">
          <input
            type="text"
            placeholder="Patient ID"
            value={formData.patientId}
            onChange={(e) => setFormData({...formData, patientId: e.target.value})}
            required
          />
          <select
            value={formData.department}
            onChange={(e) => setFormData({...formData, department: e.target.value})}
            required
          >
            <option value="">Select Department</option>
            <option value="eye">Eye</option>
            <option value="ent">ENT</option>
            <option value="skin">Skin</option>
          </select>
          <select
            value={formData.priority}
            onChange={(e) => setFormData({...formData, priority: e.target.value})}
          >
            <option value="normal">Normal</option>
            <option value="urgent">Urgent</option>
          </select>
          <button type="submit">Add to Queue</button>
          <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
        </form>
      )}

      <div className="queue-list">
        {queue.map((item, index) => (
          <div key={item.id} className="queue-item">
            <span className="queue-number">{index + 1}</span>
            <p>Patient: {item.patient_name}</p>
            <p>Department: {item.department}</p>
            <p>Priority: {item.priority}</p>
            <p>Status: {item.status}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Queue;
