import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../api/endpoints';

const HospitalOverview = () => {
  const [overview, setOverview] = useState({
    departments: {},
    recent_activity: []
  });

  useEffect(() => {
    fetchOverview();
  }, []);

  const fetchOverview = async () => {
    try {
      const response = await adminAPI.getHospitalOverview();
      setOverview(response.data);
    } catch (error) {
      console.error('Error fetching overview:', error);
    }
  };

  return (
    <div className="hospital-overview">
      <h2>Hospital Overview</h2>

      <div className="departments-grid">
        <h3>Department Statistics</h3>
        <div className="stats-grid">
          {Object.entries(overview.departments).map(([dept, stats]) => (
            <div key={dept} className="stat-card">
              <h4>{dept.toUpperCase()}</h4>
              <p>Patients: {stats.patients}</p>
              <p>Doctors: {stats.doctors}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="recent-activity">
        <h3>Recent Activity</h3>
        <div className="activity-list">
          {overview.recent_activity.map((activity, index) => (
            <div key={index} className="activity-item">
              <p><strong>{activity.patient_name}</strong> consulted with Dr. {activity.doctor_name}</p>
              <p>Department: {activity.department} | {new Date(activity.date).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HospitalOverview;
