import React, { useState, useEffect } from 'react';
import { doctorAPI, vitalsAPI } from '../../api/endpoints';
import { useParams } from 'react-router-dom';
import VitalSignsForm from '../../components/VitalSigns/VitalSignsForm';

const PatientDetails = () => {
  const { patientId } = useParams();
  const [patient, setPatient] = useState(null);
  const [consultations, setConsultations] = useState([]);
  const [vitals, setVitals] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showVitalsForm, setShowVitalsForm] = useState(false);
  const [formData, setFormData] = useState({
    symptoms: '',
    diagnosis: '',
    prescription: '',
    notes: '',
    amount: ''
  });

  useEffect(() => {
    fetchPatientDetails();
  }, [patientId]);

  const fetchPatientDetails = async () => {
    try {
      const response = await doctorAPI.getPatientHistory(patientId);
      setPatient(response.data.patient);
      setConsultations(response.data.consultations);

      const vitalsResponse = await vitalsAPI.getPatientVitals(patientId);
      setVitals(vitalsResponse.data);
    } catch (error) {
      console.error('Error fetching patient details:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await doctorAPI.saveConsultation({...formData, patient_id: patientId});
      alert('Consultation created successfully');
      setShowForm(false);
      fetchPatientDetails();
    } catch (error) {
      alert('Failed to create consultation');
    }
  };

  if (!patient) return <div className="loading">Loading patient details...</div>;

  return (
    <div className="patient-details">
      <div className="patient-info">
        <h2>{patient.name}</h2>
        <p>Age: {patient.age}</p>
        <p>Gender: {patient.gender}</p>
        <p>Phone: {patient.phone}</p>
        <p>Email: {patient.email}</p>
        <p>Address: {patient.address}</p>
      </div>

      <div className="vitals-section">
        <div className="section-header">
          <h3>Vital Signs History</h3>
          <button onClick={() => setShowVitalsForm(true)}>Record Vitals</button>
        </div>

        {showVitalsForm && (
          <VitalSignsForm 
            patientId={patientId} 
            onSave={() => {
              setShowVitalsForm(false);
              fetchPatientDetails();
            }}
          />
        )}

        <div className="vitals-list">
          {vitals.map(vital => (
            <div key={vital.id} className="vital-card">
              <p><strong>Date:</strong> {vital.created_at}</p>
              <p><strong>BP:</strong> {vital.bp_systolic}/{vital.bp_diastolic} mmHg</p>
              <p><strong>Heart Rate:</strong> {vital.heart_rate} bpm</p>
              <p><strong>Temperature:</strong> {vital.temperature}Â°C</p>
              <p><strong>Weight:</strong> {vital.weight} kg</p>
              <p><strong>Height:</strong> {vital.height} cm</p>
              <p><strong>O2 Sat:</strong> {vital.oxygen_saturation}%</p>
              <p><strong>Recorded by:</strong> {vital.recorded_by}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="consultation-section">
        <div className="section-header">
          <h3>Consultations</h3>
          <button onClick={() => setShowForm(true)}>New Consultation</button>
        </div>

        {showForm && (
          <form onSubmit={handleSubmit} className="consultation-form">
            <textarea
              placeholder="Symptoms"
              value={formData.symptoms}
              onChange={(e) => setFormData({...formData, symptoms: e.target.value})}
              required
            />
            <textarea
              placeholder="Diagnosis"
              value={formData.diagnosis}
              onChange={(e) => setFormData({...formData, diagnosis: e.target.value})}
              required
            />
            <textarea
              placeholder="Prescription"
              value={formData.prescription}
              onChange={(e) => setFormData({...formData, prescription: e.target.value})}
            />
            <textarea
              placeholder="Notes"
              value={formData.notes}
              onChange={(e) => setFormData({...formData, notes: e.target.value})}
            />
            <input
              type="number"
              placeholder="Amount"
              value={formData.amount}
              onChange={(e) => setFormData({...formData, amount: e.target.value})}
              required
            />
            <button type="submit">Save Consultation</button>
            <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
          </form>
        )}

        <div className="consultations-list">
          {consultations.map(consultation => (
            <div key={consultation.id} className="consultation-card">
              <p><strong>Date:</strong> {consultation.created_at}</p>
              <p><strong>Symptoms:</strong> {consultation.symptoms}</p>
              <p><strong>Diagnosis:</strong> {consultation.diagnosis}</p>
              <p><strong>Prescription:</strong> {consultation.prescription}</p>
              <p><strong>Notes:</strong> {consultation.notes}</p>
              <p><strong>Amount:</strong> ${consultation.amount}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PatientDetails;
