import React, { useState, useEffect } from 'react';
import { receptionistAPI } from '../../api/endpoints';
import { showToast } from '../../components/Toast/Toast';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [patientNames, setPatientNames] = useState([]);
  const [doctorNames, setDoctorNames] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    patientName: '',
    doctorName: '',
    department: '',
    date: '',
    time: ''
  });

  useEffect(() => {
    fetchAppointments();
    fetchNames();
  }, []);

  const fetchNames = async () => {
    try {
      const [patientsRes, doctorsRes] = await Promise.all([
        receptionistAPI.getPatientNames(),
        receptionistAPI.getDoctorNames()
      ]);
      setPatientNames(patientsRes.data);
      setDoctorNames(doctorsRes.data);
    } catch (error) {
      console.error('Error fetching names:', error);
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await receptionistAPI.getAppointments();
      setAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await receptionistAPI.createAppointment(formData);
      if (response.data.success) {
        showToast('Appointment scheduled successfully!', 'success');
        setShowForm(false);
        setFormData({ patientName: '', doctorName: '', department: '', date: '', time: '' });
        fetchAppointments();
      }
    } catch (error) {
      showToast('Failed to schedule appointment. Please try again.', 'error');
      console.error('Failed to schedule appointment:', error);
    }
  };

  const cancelAppointment = async (appointmentId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/receptionist/cancel-appointment/${appointmentId}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      const result = await response.json();
      if (result.success) {
        showToast('Appointment cancelled successfully!', 'success');
        fetchAppointments();
      }
    } catch (error) {
      showToast('Failed to cancel appointment', 'error');
      console.error('Error cancelling appointment:', error);
    }
  };

  return (
    <div className="appointments" style={{margin: '20px', padding: '24px'}}>
      <div className="appointments-header" style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px'}}>
        <h2 style={{color: '#ffa057', margin: '0'}}>Appointments</h2>
        <button onClick={() => setShowForm(true)} style={{padding: '10px 20px', borderRadius: '8px', border: 'none', background: '#ffa057', color: 'white', fontSize: '14px', fontWeight: '600', cursor: 'pointer'}} onMouseOver={(e) => e.target.style.background = '#ff8c42'} onMouseOut={(e) => e.target.style.background = '#ffa057'}>Schedule New</button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="appointment-form" style={{background: '#2a2d47', padding: '20px', borderRadius: '12px', marginBottom: '24px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px', alignItems: 'end'}}>
          <select
            value={formData.patientName}
            onChange={(e) => setFormData({...formData, patientName: e.target.value})}
            required
            style={{padding: '10px', borderRadius: '6px', border: '1px solid #3a3f5c', background: '#1e1e2f', color: '#ccd6f6', fontSize: '14px'}}
          >
            <option value="">Select Patient</option>
            {patientNames.map(name => (
              <option key={name} value={name}>{name}</option>
            ))}
          </select>
          <select
            value={formData.doctorName}
            onChange={(e) => setFormData({...formData, doctorName: e.target.value})}
            required
            style={{padding: '10px', borderRadius: '6px', border: '1px solid #3a3f5c', background: '#1e1e2f', color: '#ccd6f6', fontSize: '14px'}}
          >
            <option value="">Select Doctor</option>
            {doctorNames.map(name => (
              <option key={name} value={name}>{name}</option>
            ))}
          </select>
          <select
            value={formData.department}
            onChange={(e) => setFormData({...formData, department: e.target.value})}
            required
            style={{padding: '10px', borderRadius: '6px', border: '1px solid #3a3f5c', background: '#1e1e2f', color: '#ccd6f6', fontSize: '14px'}}
          >
            <option value="">Select Department</option>
            <option value="eye">Eye</option>
            <option value="ent">ENT</option>
            <option value="skin">Skin</option>
          </select>
          <input
            type="date"
            value={formData.date}
            onChange={(e) => setFormData({...formData, date: e.target.value})}
            required
            style={{padding: '10px', borderRadius: '6px', border: '1px solid #3a3f5c', background: '#1e1e2f', color: '#ccd6f6', fontSize: '14px'}}
          />
          <input
            type="time"
            value={formData.time}
            onChange={(e) => setFormData({...formData, time: e.target.value})}
            required
            style={{padding: '10px', borderRadius: '6px', border: '1px solid #3a3f5c', background: '#1e1e2f', color: '#ccd6f6', fontSize: '14px'}}
          />
          <div style={{display: 'flex', gap: '8px'}}>
            <button type="submit" style={{padding: '10px 16px', borderRadius: '6px', border: 'none', background: '#ffa057', color: 'white', fontSize: '14px', fontWeight: '600', cursor: 'pointer'}} onMouseOver={(e) => e.target.style.background = '#ff8c42'} onMouseOut={(e) => e.target.style.background = '#ffa057'}>Schedule</button>
            <button type="button" onClick={() => setShowForm(false)} style={{padding: '10px 16px', borderRadius: '6px', border: '1px solid #3a3f5c', background: 'transparent', color: '#ccd6f6', fontSize: '14px', cursor: 'pointer'}} onMouseOver={(e) => e.target.style.background = '#3a3f5c'} onMouseOut={(e) => e.target.style.background = 'transparent'}>Cancel</button>
          </div>
        </form>
      )}

      <div className="appointments-list" style={{display: 'grid', gap: '16px'}}>
        {appointments.map(appointment => (
          <div key={appointment.id} className="appointment-card" style={{background: '#2a2d47', borderRadius: '12px', padding: '20px', border: '1px solid #3a3f5c', transition: 'all 0.3s ease'}} onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'} onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}>
            <div className="appointment-details">
              <h4 style={{color: '#ccd6f6', margin: '0 0 12px 0', fontSize: '18px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '8px'}}>
                <span style={{background: '#ffa057', color: 'white', width: '32px', height: '32px', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '14px', fontWeight: '700'}}>📅</span>
                {appointment.patient_name}
              </h4>
              <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '8px'}}>
                <p style={{color: '#a0aec0', margin: '4px 0', fontSize: '14px'}}>Doctor: <span style={{color: '#ccd6f6'}}>{appointment.doctor_name}</span></p>
                <p style={{color: '#a0aec0', margin: '4px 0', fontSize: '14px'}}>Department: <span style={{color: '#ffa057'}}>{appointment.department}</span></p>
                <p style={{color: '#a0aec0', margin: '4px 0', fontSize: '14px'}}>Date: <span style={{color: '#64ffda'}}>{appointment.appointment_date}</span></p>
                <p style={{color: '#a0aec0', margin: '4px 0', fontSize: '14px'}}>Time: <span style={{color: '#64ffda'}}>{appointment.appointment_time}</span></p>
                <p style={{color: '#a0aec0', margin: '4px 0', fontSize: '14px'}}>Status: <span style={{color: appointment.status === 'scheduled' ? '#64ffda' : appointment.status === 'completed' ? '#4ade80' : '#ffa057'}}>{appointment.status}</span></p>
              </div>
            </div>
            <button 
              onClick={() => cancelAppointment(appointment.id)}
              style={{padding: '8px 16px', borderRadius: '6px', border: 'none', background: '#ff4757', color: 'white', fontSize: '14px', fontWeight: '600', cursor: 'pointer'}}
              onMouseOver={(e) => e.target.style.background = '#ff3742'}
              onMouseOut={(e) => e.target.style.background = '#ff4757'}
            >
              Cancel
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Appointments;
