import React, { useState } from 'react';
import { vitalsAPI } from '../../api/endpoints';

const VitalSignsForm = ({ patientId, onSave }) => {
  const [vitals, setVitals] = useState({
    bp_systolic: '',
    bp_diastolic: '',
    heart_rate: '',
    temperature: '',
    weight: '',
    height: '',
    oxygen_saturation: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await vitalsAPI.recordVitals(patientId, vitals);
      alert('Vital signs recorded successfully');
      onSave && onSave();
      setVitals({
        bp_systolic: '',
        bp_diastolic: '',
        heart_rate: '',
        temperature: '',
        weight: '',
        height: '',
        oxygen_saturation: ''
      });
    } catch (error) {
      alert('Failed to record vital signs');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="vitals-form">
      <h3>Record Vital Signs</h3>
      <div className="vitals-grid">
        <div className="form-group">
          <label>Blood Pressure (Systolic):</label>
          <input
            type="number"
            placeholder="120"
            value={vitals.bp_systolic}
            onChange={(e) => setVitals({...vitals, bp_systolic: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Blood Pressure (Diastolic):</label>
          <input
            type="number"
            placeholder="80"
            value={vitals.bp_diastolic}
            onChange={(e) => setVitals({...vitals, bp_diastolic: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Heart Rate (bpm):</label>
          <input
            type="number"
            placeholder="72"
            value={vitals.heart_rate}
            onChange={(e) => setVitals({...vitals, heart_rate: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Temperature (Â°C):</label>
          <input
            type="number"
            step="0.1"
            placeholder="36.5"
            value={vitals.temperature}
            onChange={(e) => setVitals({...vitals, temperature: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Weight (kg):</label>
          <input
            type="number"
            step="0.1"
            placeholder="70.0"
            value={vitals.weight}
            onChange={(e) => setVitals({...vitals, weight: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Height (cm):</label>
          <input
            type="number"
            placeholder="170"
            value={vitals.height}
            onChange={(e) => setVitals({...vitals, height: e.target.value})}
          />
        </div>
        <div className="form-group">
          <label>Oxygen Saturation (%):</label>
          <input
            type="number"
            placeholder="98"
            value={vitals.oxygen_saturation}
            onChange={(e) => setVitals({...vitals, oxygen_saturation: e.target.value})}
          />
        </div>
      </div>
      <button type="submit">Record Vitals</button>
    </form>
  );
};

export default VitalSignsForm;
