import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Sidebar.css';

const Sidebar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();

  const getMenuItems = () => {
    switch (user?.role) {
      case 'admin':
        return [
          { path: '/admin/dashboard', label: 'Dashboard', icon: 'üè†' },
          { path: '/admin/panel', label: 'Admin Panel', icon: 'üßë‚Äç‚öïÔ∏è' },
          { path: '/admin/register-doctor', label: 'Register Doctor', icon: 'üë®‚Äç‚öïÔ∏è' },
          { path: '/admin/register-receptionist', label: 'Register Receptionist', icon: 'üë©‚Äçüíº' },
          { path: '/admin/patients', label: 'All Patients', icon: 'üë•' },
          { path: '/admin/settings', label: 'System Settings', icon: '‚öôÔ∏è' }
        ];
      case 'receptionist':
        return [
          { path: '/receptionist/dashboard', label: 'Dashboard', icon: 'üè†' },
          { path: '/receptionist/register-patient', label: 'Register Patient', icon: 'üë§' },
          { path: '/receptionist/appointments', label: 'Appointments', icon: 'üìÖ' },
          { path: '/receptionist/queue', label: 'Queue', icon: '‚è≥' },
          { path: '/receptionist/billing', label: 'Billing', icon: 'üí≥' }
        ];
      case 'doctor':
        return [
          { path: '/doctor/dashboard', label: 'Dashboard', icon: 'üè†' },
          { path: '/doctor/patients', label: 'My Patients', icon: 'üë•' },
          { path: '/doctor/appointments', label: 'Appointments', icon: 'üìÖ' },
          { path: '/doctor/queue', label: 'Queue', icon: '‚è≥' },
          { path: '/change-password', label: 'Profile', icon: 'üë§' }
        ];
      default:
        return [];
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Hospital MS</h3>
        <p>{user?.name}</p>
        <span className="role-badge">{user?.role}</span>
      </div>

      <nav className="sidebar-nav">
        {getMenuItems().map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={logout} className="logout-btn">
          <span className="nav-icon">üö™</span>
          <span className="nav-label">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
