import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ExamRoom.css';

const ExamRoom = () => {
  const { patientId } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);
  const [vitals, setVitals] = useState({
    blood_pressure_systolic: '',
    blood_pressure_diastolic: '',
    heart_rate: '',
    temperature: '',
    weight: '',
    height: '',
    oxygen_saturation: ''
  });
  const [consultation, setConsultation] = useState({
    diagnosis: '',
    prescription: '',
    notes: ''
  });
  const [examData, setExamData] = useState({});

  useEffect(() => {
    fetchPatientDetails();
  }, [patientId]);

  const fetchPatientDetails = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/doctor/patient-details/${patientId}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setPatient(data);
    } catch (error) {
      console.error('Error fetching patient details:', error);
    }
  };

  const recordVitals = async () => {
    try {
      await fetch(`http://localhost:5000/api/vitals/record/${patientId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(vitals)
      });
      alert('Vitals recorded successfully');
    } catch (error) {
      console.error('Error recording vitals:', error);
    }
  };

  const saveConsultation = async () => {
    try {
      await fetch('http://localhost:5000/api/doctor/save-consultation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ 
          ...consultation, 
          patient_id: parseInt(patientId),
          exam_data: JSON.stringify(examData),
          amount: 50
        })
      });
      alert('Consultation saved successfully');
      navigate('/doctor/dashboard');
    } catch (error) {
      console.error('Error saving consultation:', error);
    }
  };

  const renderDepartmentSpecificForm = () => {
    if (!patient) return null;

    switch(patient.department) {
      case 'eye':
        return renderEyeForm();
      case 'ent':
        return renderENTForm();
      case 'skin':
        return renderSkinForm();
      default:
        return null;
    }
  };

  const renderEyeForm = () => (
    <div className="eye-exam-form">
      <div className="eye-section">
        <h4>üëÅ Right Eye (OD)</h4>
        <input placeholder="Visual Acuity" onChange={(e) => setExamData({...examData, od_visual_acuity: e.target.value})} />
        <input placeholder="Cornea" onChange={(e) => setExamData({...examData, od_cornea: e.target.value})} />
        <input placeholder="Lens" onChange={(e) => setExamData({...examData, od_lens: e.target.value})} />
        <input placeholder="Retina" onChange={(e) => setExamData({...examData, od_retina: e.target.value})} />
        <input placeholder="Intraocular Pressure" onChange={(e) => setExamData({...examData, od_pressure: e.target.value})} />
        <input placeholder="Refraction" onChange={(e) => setExamData({...examData, od_refraction: e.target.value})} />
      </div>
      <div className="eye-section">
        <h4>üëÅ Left Eye (OS)</h4>
        <input placeholder="Visual Acuity" onChange={(e) => setExamData({...examData, os_visual_acuity: e.target.value})} />
        <input placeholder="Cornea" onChange={(e) => setExamData({...examData, os_cornea: e.target.value})} />
        <input placeholder="Lens" onChange={(e) => setExamData({...examData, os_lens: e.target.value})} />
        <input placeholder="Retina" onChange={(e) => setExamData({...examData, os_retina: e.target.value})} />
        <input placeholder="Intraocular Pressure" onChange={(e) => setExamData({...examData, os_pressure: e.target.value})} />
        <input placeholder="Refraction" onChange={(e) => setExamData({...examData, os_refraction: e.target.value})} />
      </div>
    </div>
  );

  const renderENTForm = () => (
    <div className="ent-exam-form">
      <h4>üëÇ ENT Examination</h4>
      <input placeholder="Right Ear Exam" onChange={(e) => setExamData({...examData, right_ear: e.target.value})} />
      <input placeholder="Left Ear Exam" onChange={(e) => setExamData({...examData, left_ear: e.target.value})} />
      <input placeholder="Nose Exam" onChange={(e) => setExamData({...examData, nose_exam: e.target.value})} />
      <input placeholder="Throat Exam" onChange={(e) => setExamData({...examData, throat_exam: e.target.value})} />
      <input placeholder="Hearing Test" onChange={(e) => setExamData({...examData, hearing_test: e.target.value})} />
    </div>
  );

  const renderSkinForm = () => (
    <div className="skin-exam-form">
      <h4>ü©∫ Skin Examination</h4>
      <input placeholder="Affected Area" onChange={(e) => setExamData({...examData, affected_area: e.target.value})} />
      <input placeholder="Lesion Type" onChange={(e) => setExamData({...examData, lesion_type: e.target.value})} />
      <input placeholder="Color/Texture" onChange={(e) => setExamData({...examData, color_texture: e.target.value})} />
      <textarea placeholder="Symptoms" onChange={(e) => setExamData({...examData, symptoms: e.target.value})} />
    </div>
  );

  if (!patient) return <div>Loading...</div>;

  return (
    <div className="exam-room">
      <div className="exam-header">
        <button onClick={() => navigate(-1)}>‚Üê Back</button>
        <h2>Examination - {patient.name}</h2>
      </div>

      <div className="exam-sections">
        <div className="vitals-section">
          <h3>Record Vitals</h3>
          <div className="vitals-form">
            <input
              type="number"
              placeholder="Systolic BP"
              value={vitals.blood_pressure_systolic}
              onChange={(e) => setVitals({...vitals, blood_pressure_systolic: e.target.value})}
            />
            <input
              type="number"
              placeholder="Diastolic BP"
              value={vitals.blood_pressure_diastolic}
              onChange={(e) => setVitals({...vitals, blood_pressure_diastolic: e.target.value})}
            />
            <input
              type="number"
              placeholder="Heart Rate"
              value={vitals.heart_rate}
              onChange={(e) => setVitals({...vitals, heart_rate: e.target.value})}
            />
            <input
              type="number"
              placeholder="Temperature (¬∞C)"
              value={vitals.temperature}
              onChange={(e) => setVitals({...vitals, temperature: e.target.value})}
            />
            <input
              type="number"
              placeholder="Weight (kg)"
              value={vitals.weight}
              onChange={(e) => setVitals({...vitals, weight: e.target.value})}
            />
            <input
              type="number"
              placeholder="Height (cm)"
              value={vitals.height}
              onChange={(e) => setVitals({...vitals, height: e.target.value})}
            />
            <input
              type="number"
              placeholder="Oxygen Saturation (%)"
              value={vitals.oxygen_saturation}
              onChange={(e) => setVitals({...vitals, oxygen_saturation: e.target.value})}
            />
            <button onClick={recordVitals}>Record Vitals</button>
          </div>
        </div>

        <div className="consultation-section">
          <h3>Consultation</h3>
          {renderDepartmentSpecificForm()}
          <div className="general-section">
            <h4>General</h4>
            <textarea
              placeholder="Diagnosis"
              value={consultation.diagnosis}
              onChange={(e) => setConsultation({...consultation, diagnosis: e.target.value})}
            />
            <textarea
              placeholder="Prescription (medicines + dosage)"
              value={consultation.prescription}
              onChange={(e) => setConsultation({...consultation, prescription: e.target.value})}
            />
            <textarea
              placeholder="Notes / Recommendations"
              value={consultation.notes}
              onChange={(e) => setConsultation({...consultation, notes: e.target.value})}
            />
            <button onClick={saveConsultation}>Save Consultation</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExamRoom;
