import React, { useState } from 'react';
import { receptionistAPI } from '../../api/endpoints';
import VitalSignsForm from '../../components/VitalSigns/VitalSignsForm';

const RegisterPatient = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    age: '',
    gender: '',
    address: '',
    department: ''
  });
  const [showVitals, setShowVitals] = useState(false);
  const [patientId, setPatientId] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await receptionistAPI.registerPatient(formData);
      alert('Patient registered successfully');
      setPatientId(response.data.patient_id);
      setShowVitals(true);
    } catch (error) {
      alert('Registration failed');
    }
  };

  const handleVitalsSaved = () => {
    setShowVitals(false);
    setFormData({ name: '', email: '', phone: '', age: '', gender: '', address: '', department: '' });
    setPatientId(null);
  };

  return (
    <div className="register-patient">
      <h2>Register Patient</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({...formData, email: e.target.value})}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          value={formData.phone}
          onChange={(e) => setFormData({...formData, phone: e.target.value})}
          required
        />
        <input
          type="number"
          placeholder="Age"
          value={formData.age}
          onChange={(e) => setFormData({...formData, age: e.target.value})}
          required
        />
        <select
          value={formData.gender}
          onChange={(e) => setFormData({...formData, gender: e.target.value})}
          required
        >
          <option value="">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
        <textarea
          placeholder="Address"
          value={formData.address}
          onChange={(e) => setFormData({...formData, address: e.target.value})}
          required
        />
        <select
          value={formData.department}
          onChange={(e) => setFormData({...formData, department: e.target.value})}
          required
        >
          <option value="">Select Department</option>
          <option value="eye">Eye</option>
          <option value="ent">ENT</option>
          <option value="skin">Skin</option>
        </select>
        <button type="submit">Register Patient</button>
      </form>

      {showVitals && patientId && (
        <VitalSignsForm 
          patientId={patientId} 
          onSave={handleVitalsSaved}
        />
      )}
    </div>
  );
};

export default RegisterPatient;
