import React, { useState, useEffect } from 'react';
import StaffTable from '../../components/Admin/StaffTable';
import AddStaffModal from '../../components/Admin/AddStaffModal';
import './AdminPanel.css';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('staff');
  const [staffData, setStaffData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showRoleModal, setShowRoleModal] = useState(false);
  const [showPermissionModal, setShowPermissionModal] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);
  const [filters, setFilters] = useState({
    search: '',
    department: 'all',
    role: 'all',
    status: 'all'
  });

  useEffect(() => {
    fetchStaffData();
  }, []);

  const fetchStaffData = async () => {
    try {
      const response = await fetch('/api/admin/all-staff');
      const data = await response.json();
      setStaffData(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching staff data:', error);
      setStaffData([]);
      setLoading(false);
    }
  };

  const handleAddStaff = async (newStaff) => {
    try {
      const response = await fetch('/api/admin/add-staff', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newStaff)
      });
      if (response.ok) {
        await fetchStaffData();
        setShowAddModal(false);
        alert('Staff member added successfully!');
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to add staff member');
      }
    } catch (error) {
      console.error('Error adding staff:', error);
      alert('Failed to add staff member');
    }
  };

  const handleEditPermissions = (role) => {
    setSelectedRole(role);
    setShowPermissionModal(true);
  };

  const handleAddRole = () => {
    setShowRoleModal(true);
  };

  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({ ...prev, [filterType]: value }));
  };

  const filteredStaff = staffData.filter(staff => {
    const matchesSearch = staff.name.toLowerCase().includes(filters.search.toLowerCase()) ||
                         staff.email.toLowerCase().includes(filters.search.toLowerCase());
    const matchesDepartment = filters.department === 'all' || staff.department?.toLowerCase() === filters.department;
    const matchesRole = filters.role === 'all' || staff.role.toLowerCase() === filters.role;
    const matchesStatus = filters.status === 'all' || staff.status.toLowerCase() === filters.status;

    return matchesSearch && matchesDepartment && matchesRole && matchesStatus;
  });

  const statsCards = [
    { title: 'Total Staff', value: staffData.length, color: 'default' },
    { title: 'Active Staff', value: staffData.filter(s => s.status === 'Active').length, color: 'green' },
    { title: 'Doctors', value: staffData.filter(s => s.role === 'doctor').length, color: 'blue' },
    { title: 'Receptionists', value: staffData.filter(s => s.role === 'receptionist').length, color: 'purple' },
    { title: 'Pending Approvals', value: staffData.filter(s => s.status === 'Pending').length, color: 'yellow' }
  ];

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>Admin Panel</h1>
        <p>Manage doctors, receptionists, departments, roles, and permissions</p>
      </div>

      <div className="admin-tabs">
        <button className={`tab-button ${activeTab === 'staff' ? 'active' : ''}`} onClick={() => setActiveTab('staff')}>
          Doctors & Staff
        </button>
        <button className={`tab-button ${activeTab === 'roles' ? 'active' : ''}`} onClick={() => setActiveTab('roles')}>
          Roles & Permissions
        </button>
      </div>

      {activeTab === 'staff' && (
        <div className="staff-tab">
          <div className="stats-cards">
            {statsCards.map((card, index) => (
              <div key={index} className={`stats-card ${card.color}`}>
                <div className="stats-value">{card.value}</div>
                <div className="stats-title">{card.title}</div>
                <div className={`stats-indicator ${card.color}`}></div>
              </div>
            ))}
          </div>

          <div className="filter-bar">
            <div className="search-input">
              <input
                type="text"
                placeholder="Search doctors or receptionists..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
              />
            </div>

            <select value={filters.department} onChange={(e) => handleFilterChange('department', e.target.value)}>
              <option value="all">All Departments</option>
              <option value="eye">Eye</option>
              <option value="ent">ENT</option>
              <option value="skin">Skin</option>
            </select>

            <select value={filters.role} onChange={(e) => handleFilterChange('role', e.target.value)}>
              <option value="all">All Roles</option>
              <option value="doctor">Doctor</option>
              <option value="receptionist">Receptionist</option>
            </select>

            <select value={filters.status} onChange={(e) => handleFilterChange('status', e.target.value)}>
              <option value="all">All Status</option>
              <option value="active">Active</option>
              <option value="pending">Pending</option>
              <option value="inactive">Inactive</option>
            </select>

            <button className="add-staff-btn" onClick={() => setShowAddModal(true)}>+ Add Staff</button>
          </div>

          <StaffTable 
            staffData={filteredStaff} 
            loading={loading}
            totalCount={staffData.length}
            filteredCount={filteredStaff.length}
          />
        </div>
      )}

      {activeTab === 'roles' && (
        <div className="roles-tab">
          <div className="roles-header">
            <h3>Roles & Permissions</h3>
            <button className="add-role-btn" onClick={handleAddRole}>+ Add Role</button>
          </div>

          <div className="roles-grid">
            {[
              { title: 'Eye Doctor', desc: 'View and update patient eye records, add prescriptions' },
              { title: 'ENT Doctor', desc: 'Manage ENT patient records' },
              { title: 'Skin Doctor', desc: 'Manage skin-related patients and prescriptions' },
              { title: 'Receptionist', desc: 'Register patients, view queues, schedule appointments' },
              { title: 'Admin', desc: 'Full access to all hospital management features' }
            ].map((role, index) => (
              <div key={index} className="role-card">
                <h4>{role.title}</h4>
                <p>{role.desc}</p>
                <button className="edit-role-btn" onClick={() => handleEditPermissions(role)}>Edit Permissions</button>
              </div>
            ))}
          </div>
        </div>
      )}

      {showAddModal && (
        <AddStaffModal 
          onClose={() => setShowAddModal(false)}
          onAdd={handleAddStaff}
        />
      )}

      {showRoleModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Add New Role</h3>
              <button className="close-btn" onClick={() => setShowRoleModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <form onSubmit={(e) => { e.preventDefault(); alert('Role creation functionality coming soon!'); setShowRoleModal(false); }}>
                <div className="form-group">
                  <label>Role Name</label>
                  <input type="text" required />
                </div>
                <div className="form-group">
                  <label>Description</label>
                  <textarea required></textarea>
                </div>
                <div className="form-actions">
                  <button type="button" onClick={() => setShowRoleModal(false)} className="cancel-btn">Cancel</button>
                  <button type="submit" className="submit-btn">Create Role</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {showPermissionModal && selectedRole && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Edit Permissions - {selectedRole.title}</h3>
              <button className="close-btn" onClick={() => setShowPermissionModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <div className="permissions-list">
                {['View Patients', 'Add Patients', 'Edit Patients', 'Delete Patients', 'View Appointments', 'Schedule Appointments', 'Cancel Appointments', 'View Billing', 'Process Payments', 'Generate Reports'].map((permission, index) => (
                  <div key={index} className="permission-item">
                    <label>
                      <input type="checkbox" defaultChecked={index < 5} />
                      {permission}
                    </label>
                  </div>
                ))}
              </div>
              <div className="form-actions">
                <button onClick={() => setShowPermissionModal(false)} className="cancel-btn">Cancel</button>
                <button onClick={() => { alert('Permissions updated!'); setShowPermissionModal(false); }} className="submit-btn">Save Changes</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
