import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import DateFilter from '../../components/DateFilter/DateFilter';
import './Dashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalDoctors: 0,
    receptionists: 0,
    totalPatients: 0,
    todayRevenue: 0
  });

  const [patientFlowData, setPatientFlowData] = useState([]);
  const [dateRange, setDateRange] = useState({ 
    startDate: new Date().toISOString().split('T')[0], 
    endDate: new Date().toISOString().split('T')[0], 
    range: 'today' 
  });

  const handleDateChange = (newDateRange) => {
    setDateRange(newDateRange);
    fetchDashboardData(newDateRange.startDate, newDateRange.endDate);
  };

  const [recentAppointments, setRecentAppointments] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, [dateRange]);

  const fetchDashboardData = async (startDate = dateRange.startDate, endDate = dateRange.endDate) => {
    try {
      const response = await fetch(`http://localhost:5000/api/admin/dashboard-summary?startDate=${startDate}&endDate=${endDate}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setStats(data.stats);
      setPatientFlowData(data.patientFlow || []);
      setRecentAppointments(data.recentAppointments || []);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  return (
    <div className="page-container">
      <div className="page-header">
        <h1>Admin Dashboard</h1>
        <p>Welcome back, Admin! Here's the latest</p>
      </div>

      <DateFilter onDateChange={handleDateChange} selectedRange={dateRange.range} />

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">ðŸ‘¥</div>
          <div className="stat-content">
            <div className="stat-value">{stats.totalDoctors}</div>
            <div className="stat-label">Total Doctors</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">ðŸ“‹</div>
          <div className="stat-content">
            <div className="stat-value">{stats.receptionists}</div>
            <div className="stat-label">Receptionists</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">ðŸ‘¥</div>
          <div className="stat-content">
            <div className="stat-value">{stats.totalPatients}</div>
            <div className="stat-label">Total Patients</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">ðŸ’°</div>
          <div className="stat-content">
            <div className="stat-value">{stats.todayRevenue?.toLocaleString() || 0}</div>
            <div className="stat-label">Revenue (today)</div>
          </div>
        </div>
      </div>

      <div className="charts-section">
        <div className="card" style={{background: '#2a2d47', borderRadius: '12px', padding: '20px', border: '1px solid #3a3f5c'}}>
          <h3 style={{color: '#ccd6f6', fontSize: '16px', fontWeight: '600', margin: '0 0 16px 0'}}>Patient Flow</h3>
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={patientFlowData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#2a2d47" />
              <XAxis dataKey="day" axisLine={false} tickLine={false} tick={{ fill: '#8892b0', fontSize: 12 }} />
              <YAxis axisLine={false} tickLine={false} tick={{ fill: '#8892b0', fontSize: 12 }} />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: '#1e1e2f', 
                  border: '1px solid #2a2d47', 
                  borderRadius: '8px',
                  color: '#ccd6f6'
                }} 
              />
              <Line 
                type="monotone" 
                dataKey="patients" 
                stroke="#64ffda" 
                strokeWidth={2} 
                dot={{ fill: '#64ffda', strokeWidth: 2, r: 4 }}
                activeDot={{ r: 6, fill: '#64ffda' }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="recent-section">
        <div className="card" style={{background: '#2a2d47', borderRadius: '12px', padding: '20px', border: '1px solid #3a3f5c'}}>
          <h3 style={{color: '#ccd6f6', fontSize: '16px', fontWeight: '600', margin: '0 0 16px 0'}}>Recent Appointments</h3>
          <div className="table-header" style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', padding: '8px 0', borderBottom: '1px solid #3a3f5c', marginBottom: '12px'}}>
            <span style={{color: '#a0aec0', fontSize: '12px', fontWeight: '600', textTransform: 'uppercase'}}>Doctor</span>
            <span style={{color: '#a0aec0', fontSize: '12px', fontWeight: '600', textTransform: 'uppercase'}}>Department</span>
          </div>
          {recentAppointments.length === 0 ? (
            <div className="no-data" style={{textAlign: 'center', padding: '40px 20px', color: '#a0aec0'}}>
              <p style={{margin: '0', fontStyle: 'italic'}}>No appointments yet</p>
            </div>
          ) : (
            recentAppointments.map((apt, index) => (
              <div key={index} className="table-row" style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', padding: '8px 0', borderBottom: index === recentAppointments.length - 1 ? 'none' : '1px solid #3a3f5c'}}>
                <span style={{color: '#ccd6f6', fontWeight: '500'}}>{apt.doctor}</span>
                <span style={{color: '#ffa057'}}>{apt.department}</span>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
