import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const PatientDetails = () => {
  const { patientId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [patient, setPatient] = useState(null);
  const [history, setHistory] = useState([]);
  const [vitals, setVitals] = useState(null);
  const [activeTab, setActiveTab] = useState('details');

  useEffect(() => {
    fetchPatientDetails();
    if (user.role === 'doctor' || user.role === 'admin') {
      fetchPatientHistory();
      fetchLatestVitals();
    }
  }, [patientId]);

  const fetchPatientDetails = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/${user.role}/patient-details/${patientId}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setPatient(data);
    } catch (error) {
      console.error('Error fetching patient details:', error);
    }
  };

  const fetchPatientHistory = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/doctor/patient-history/${patientId}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setHistory(data.consultations || []);
    } catch (error) {
      console.error('Error fetching patient history:', error);
      setHistory([]);
    }
  };

  const fetchLatestVitals = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/vitals/latest/${patientId}`, {
        credentials: 'include'
      });
      const data = await response.json();
      setVitals(data);
    } catch (error) {
      console.error('Error fetching vitals:', error);
    }
  };

  const startExam = () => {
    navigate(`/doctor/exam/${patientId}`);
  };

  if (!patient) return <div>Loading...</div>;

  return (
    <div className="patient-details">
      <div className="patient-header">
        <button onClick={() => navigate(-1)}>← Back</button>
        <h2>{patient.name}</h2>
        {user.role === 'doctor' && (
          <button onClick={startExam} className="start-exam-btn">Start Examination</button>
        )}
      </div>

      <div className="tabs">
        <button className={activeTab === 'details' ? 'active' : ''} onClick={() => setActiveTab('details')}>
          Details
        </button>
        {(user.role === 'doctor' || user.role === 'admin') && (
          <button className={activeTab === 'history' ? 'active' : ''} onClick={() => setActiveTab('history')}>
            History
          </button>
        )}
        {(user.role === 'receptionist' || user.role === 'admin') && (
          <button className={activeTab === 'billing' ? 'active' : ''} onClick={() => setActiveTab('billing')}>
            Billing
          </button>
        )}
      </div>

      {activeTab === 'details' && (
        <div className="patient-info">
          <p><strong>Age:</strong> {patient.age}</p>
          <p><strong>Gender:</strong> {patient.gender}</p>
          <p><strong>Phone:</strong> {patient.phone}</p>
          <p><strong>Department:</strong> {patient.department}</p>
          <p><strong>Address:</strong> {patient.address}</p>

          {vitals && (
            <div className="latest-vitals">
              <h3>Latest Vitals</h3>
              <p>BP: {vitals.blood_pressure_systolic}/{vitals.blood_pressure_diastolic}</p>
              <p>Heart Rate: {vitals.heart_rate}</p>
              <p>Temperature: {vitals.temperature}°C</p>
              <p>Weight: {vitals.weight}kg</p>
            </div>
          )}
        </div>
      )}

      {activeTab === 'history' && (
        <div className="patient-history">
          <h3>Medical History</h3>
          {Array.isArray(history) && history.length > 0 ? history.map((record, index) => (
            <div key={index} className="history-record">
              <p><strong>Date:</strong> {new Date(record.created_at).toLocaleDateString()}</p>
              <p><strong>Doctor:</strong> {record.doctor_name}</p>
              <p><strong>Diagnosis:</strong> {record.diagnosis}</p>
              <p><strong>Prescription:</strong> {record.prescription}</p>
            </div>
          )) : <p>No medical history available</p>}
        </div>
      )}

      {activeTab === 'billing' && (
        <div className="patient-billing">
          <h3>Billing Information</h3>
          {patient.bills?.map((bill, index) => (
            <div key={index} className="bill-record">
              <p><strong>Amount:</strong> ${bill.amount}</p>
              <p><strong>Status:</strong> {bill.status}</p>
              <p><strong>Date:</strong> {new Date(bill.created_at).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PatientDetails;
